INSERT INTO `brands` VALUES ('30', 'Hermes', 'Nhà sản xuất Hermes sở hữu 45 loại nước hoa trong bộ sưu tập nước hoa chính của chúng ta. Hermes là công ty sản xuất nước hoa lâu đời. Những loại nước hoa đầu tiên của công ty được sản xuất từ năm 1951 và những loại mới nhất được sản xuất vào năm 2010. Để sản xuất các loại nước hoa có thương hiệu Hermes, Hermes cộng tác với các nhà sản xuất nước hoa Maurice Roucel, Jean-Claude Ellena, Maurice Maurin, Francois Maurin, Jean-Louis Sieuzac, Guy Robert, Fabrice Pellegrin, Jean Guichard, Ralf Schwieger, Nathalie Feisthauer, Edmond Roudnitska, Francoise Caron, Olivia Giacobetti, Akiko Kamei và Gilles Romey.', null, 'images/brands/daff98ff12a0ba310dd0268337a700a8.gif', 'images/brands/be39405df949f5b3a8e0eb8d841d6f96.gif');
