INSERT INTO `products` VALUES ('5', 'Visit Deodorant Stick\r\n', 'images/products/Azzaro/Azzaro_Visit_Deodorant_Stic.gif', '53', '7', 'Azzaro vừa trình làng loại nước hoa mới trong bộ sưu tập nước hoa Chrome mang tên Chrome United. Thành phần chính của phiên bản này là gỗ, được lấy cảm hứng từ khoản thời gian êm đẹp, những khoảnh khắc thú vị cùng với bạn bè cho cuộc sống được thoải mái và thú vị hơn.\r\n\r\nHương đầu của Azzaro Chrome United được làm mới từ các thành phần đặc trưng của cam, quýt bergamot, kết hợp với các phụ vị (ngò thơm và hạt tiêu Szechuan). Hương giữa mãnh liệt của trà đen Ceylon và lá violet, hương cuối bởi gỗ tuyết tùng và xạ hương. Người đại diện cho nước hoa là Richard Ibanez.\r\n\r\nĐược giới thiệu vào năm 2013 với các dung tích 50ml và 100m Eau de toillete', '5', null, '2015-09-24', '');
INSERT INTO `products` VALUES ('6', 'Chrome United Azzaro for men', 'images/products/Azzaro/chrome-united-azzaro-for-men-1433750643.png', '6', '567', 'Azzaro vừa trình làng loại nước hoa mới trong bộ sưu tập nước hoa Chrome mang tên Chrome United. Thành phần chính của phiên bản này là gỗ, được lấy cảm hứng từ khoản thời gian êm đẹp, những khoảnh khắc thú vị cùng với bạn bè cho cuộc sống được thoải mái và thú vị hơn.', '6', null, '2015-09-24', null);
INSERT INTO `products` VALUES ('7', 'Azzaro Pink Tonic', 'images/products/Azzaro/pink-toc.gif', '56', '37', 'Một mùi hương của cocktail hoa quả rạng rỡ và có thể làm bạn thèm thuồng. Một mùi hương tươi mới và đầy năng lượng! Pink Tonic là thứ đồ trang sức không thể thiếu được của những cô gái , những người biết chăm chút bản thân mình và mang lại những màu sắc mới mẻ cho nó . Hấp dẫn, sảng khoái và ngập tràn lạc quan , loại nước hoa này chính là sự ngưỡng mộ của Azzaro đối với tuổi trẻ và nét quyến rũ của người phụ nữ .', '7', null, '2015-09-24', null);
