INSERT INTO `products` VALUES ('11', 'Banana Republic M', 'images/products/Banana Republic/2DED7359_image.gif', '314', null, 'Được giới thiệu năm 1996, Banana Republic for Men có hương thơm sắc sảo mùi hoa cỏ và cũng rất nam tính. Thành phần cấu tạo của nó gồm có sự ấm áp của gia vị, sự tươi mát của các loại trái cây như cam, quýt và mùi của gỗ.\r\n\r\nPhong cách: Gợi cảm, thanh lịch\r\n\r\nNên dùng vào: Hàng ngày\r\n\r\nNăm sản xuất: 2006', '11', null, '2015-09-24', '');
