INSERT INTO `products` VALUES ('19', 'Bebe Gold\r\n', 'images/products/BeBe/1345105401 (1).png', '135', null, 'Các nhà thời trang của Bebe ra mắt loại nước hoa thứ ba. Sau sự ra đời của Bebe năm 2009 và Sheer Bebe năm 2010, Gold Bebe được giới thiệu với mùi thơm lấp lánh của gỗ, mang phong cách phương đông. Bebe Gold được thiết kế cho phụ nữ quyến rũ và bí ẩn, đầy đủ sự tự tin.', '19', null, '2015-11-11', null);
INSERT INTO `products` VALUES ('20', 'Giorgio G', 'images/product/Beverly Hills/1229393689Bevery-Giogio-G.gif', '2', '653', 'Giorgio G đem đến cho những ai yếu quí hương thơm tươi mát dịu mát, dịu dàng,... một cảm giác thăng hoa, tự tin. Ngoài ra, với kiểu dáng chai được thiết kế theo phong cách mềm mai, uyển chuyển nhưng không kém phần sang trọng. Sản phẩm ra đời vào năm 1999.', '20', null, '2015-11-11', '');
