INSERT INTO `products` VALUES ('24', 'Miss Boucheron', 'images/products/Boucheron/1342601123.png', null, '5', 'Miss Boucheron - Trang sức dành cho nữ hoàng. Sản phẩm nổi tiếng của Pháp. Với ý tưởng từ mặt dây chuyền của các cô gái trẻ, nhà thiết kế đã cho ra đời hình dạng chai nước hoa giống như mặt dây chuyền với mong muốn là trang sức cho bạn ngày càng rạng rỡ và quyến rũ một cách sang trọng.', '24', null, '2015-11-11', '');
INSERT INTO `products` VALUES ('25', 'Boucheron Pour Homme\r\n', 'images/products/Boucheron/1342599736.png', '3', '678', 'Được giới thiệu vào năm 1999, Boucheron Pour Homme là loại nước hoa có tươi mát nhưng nồng ấm của gỗ, thể hiện nét nam tính và phong cách sang trọng. Boucheron Pour Homme được chứa đựng trong một chiếc lọ được nhận giải thưởng về thiết kế, và vẫn giữ phong cách truyền thống của nước hoa hãng Boucheron. Nó gợi lên hình ảnh những viên đá pha lê trong vắt và đá Xaphia Burmese.', '25', null, '2015-11-11', null);
