INSERT INTO `products` VALUES ('65', 'Anais Anais\r\n', 'images/products/Cacharel/scarlett-copy.jpeg', '34', null, ' Cùng nhãn hiệu: Cacharel\r\nAnais Anais là loại nước hoa dành cho những cô gái trẻ trung luôn muốn tìm kiếm những cảm xúc êm dịu, gợi cảm và nữ tính. Với hương thơm đầu xanh mát có chút ẩn hiện của hương hoa thủy tiên và kim ngân, Anais Anais đem đến cho bạn một ấn tượng ban đầu thật phấn khởi; hương thơm giữa được xây dựng trên nền hương hoa lily cùng những nốt linh lan, hoa hồng, ngọc lan tây và hoa lài; cuối cùng là chuỗi hương gỗ và hổ phách.\r\n\r\nMùi hương đặc trưng:\r\n\r\nCam Bêgamốt, Thủy tiên, Kim ngân, Lily, Linh lan, Hoa hồng, Ngọc lan tây, Tuyết tùng, Đàn hương, Hổ phách, Rêu sồi.\r\n\r\nPhong cách:\r\n\r\nGợi cảm, nữ tính\r\n\r\nNhóm hương:\r\n\r\nFloral – Woody', '28', null, '2015-09-24', null);
