INSERT INTO `products` VALUES ('67', 'Promesse', 'images/products/Cacharel/Cacharel_Promesse.jpeg', '357', '56', 'Cùng nhãn hiệu: Cacharel\r\nPromesse là loại nước thơm mang hương hoa quả long lanh, vốn được tạo ra dành riêng cho nh ững người phụ nữ say mê. Mùi hương lóng lánh của chanh cam tươi hòa lẫn với hoa nhài và hoa phong lan kết hợp tuyệt với mùi quả mâm xôi chín ngẫu. Hương thơm này kết thúc với một chút lắng đọng của vị hổ phách nhẹ, xạ hương, gỗ đàn hương, và tuyết tùng đầy gợi cảm.\r\n\r\nMùi hương đặc trưng:\r\n\r\nQuýt, chanh yên, hoa nhài sambac, hoa phong lan, cây mâm xôi, hổ phách, xạ hương, gỗ đàn hương, tuyết tùng.\r\n\r\nPhong cách:\r\n\r\nLấp lánh. Tươi sáng. Gợi cảm.', '30', null, '2015-11-11', null);
