INSERT INTO `products` VALUES ('78', 'Forbidden Euphoria\r\n', 'images/products/Calvin Klein/1342846853.png', '453', null, 'Cùng nhãn hiệu: Calvin Klein\r\nMột cách giải thích trẻ trung của sự hưng phấn, mùi hương hoa trái cây tạo nên sự quyến rũ hiện đại, tươi mát, với một sự pha trộn đầy bí ẩn. Với hương đầu của sự pha trộn của hương quýt và mâm xôi đá. Hương giữa như thôi miên bởi hoa hổ lan và hoa nhài. Hương cuối là một sự pha trộn của hoắc hương, xạ hương và gỗ cashmere tạo nên một hương cỏ lôi cuốn.\r\n\r\nMùi hương đặc trưng:\r\n\r\nQuýt, hoa đào, mâm xôi đá, hồng mẫu đơn, hoa hổ lan, nhài, xạ hương, hoắc hương, gỗ Cashmere .\r\n\r\nPhong cách:\r\n\r\nTinh tế, tự tin.', '4', null, '2015-11-11', null);
