INSERT INTO `products` VALUES ('92', '212 Splash for Man\r\n', 'images/products/Carolina Herrera/212sexymens.gif', '3', null, 'Cùng nhãn hiệu: Carolina Herrera\r\nVới 212 Splash, Carolina Herrera đã tạo nên một ốc đảo tươi mát ngay giữa lòng thành phố. Với thành công đó, Herrera tiếp tục giới thiệu 212 Men Splash, bổ sung thêm một hương thơm sảng khoái, thú vị và sinh động vào bộ sưu tập của mình. Cũng với một thiết kế như một lon soda mát lạnh, 212 Men Splash đầy sức sống với hương bạc hà và gừng tươi nhưng vẫn giữ nguyên được vẻ nam tính của 212 Men. Hương thơm tươi mát hòa quyện cùng hương nồng nàn của gỗ đàn hương và hương trầm đã mang đến cho 212 Men Splash sự quyến rũ thật tinh tế.\r\n\r\nHương đặc trưng: Cam Bêgamốt, Bạc hà, Gừng, Hạt nhục đậu khấu, Bạch đậu khấu, Cây dành dành, Gổ đàn hương, Xạ hương, Hương trầm.\r\n\r\nPhong cách: Táo bạo, tươi mát, hấp dẫn', '18', null, '2015-11-11', null);
