INSERT INTO `products` VALUES ('98', 'Allure Sensuelle\r\n', 'images/products/Chanel/1342580628.png', '45', null, 'Cùng nhãn hiệu: Chanel\r\nVẻ đẹp tiềm ẩn của Phương Đông ẩn chứa trong mùi hương nồng nàn. Với Allure Sensuelle, Chanel đã giúp cho sự quyến rũ tiềm ẩn hoà nhịp cùng vẻ đẹp rạng ngời. Được tạo ra bởi Jacques Polge, loại nước hoa này mang đến sức cuốn hút huyền bí với những mùi hương đặc trưng như hổ phách - một mùi hương thảo dược quyến rũ dạng bột mịn, hoà quyện cùng hương thơm của tiêu hồng và hương trầm. Một loại nước hoa huyền bí, đầy gợi tình, được hoá thân trong sự quyến rũ của nữ diễn viên Anna Mouglalis. Allure Sensuelle là một phiên bản khác của Allure, kết hợp thành công vẻ đẹp kín đáo, tiềm ẩn mà vẫn vô cùng đắm say và quyến rũ. Được miêu tả như hương Phương Đông ngọt ngào, đằm thắm, loại nước hoa này được tạo ra với 6 phong cách đặc trưng: Hương Phương Đông với Vani Pháp và hổ phách - một mùi hương đầy gợi tình. Tươi trẻ với hương cam. Hương hoa bất tận với hoa hồng, hoa nhài, và iris. Mùi của hoa quả ngọt ngào. Mùi gỗ đặc trưng với Vetiver. Hương nhiệt đới nóng bỏng với tiêu đỏ và hương trầm. Bạn sẽ nhận ra những những đặc trưng riêng biệt làm nên Allure bên dưới lớp vỏ màu hồng đầy gợi cảm.\r\n\r\nMùi hương đặc trưng:\r\n\r\nBecgamốt, Quít, Tiêu đỏ, Hoa nhài, Hồng, Iris, Hoa quả, Vetiver, Vani Pháp, Hổ phách, Hương trầm.\r\n\r\nPhong cách:\r\n\r\nGợi cảm, sang trọng.', '24', null, '2015-11-11', null);
INSERT INTO `products` VALUES ('99', 'Chance Eau de parfum\r\n', 'images/products/Chanel/1342169797.png', '5324', '47', 'Cùng nhãn hiệu: Chanel\r\nChance, nghĩa là May mắn, chính là cuộc sống của bạn. May mắn đến với bạn khi bạn không mong đợi nó xảy ra nhất. Cũng như thế, Chanel Chance đến với bạn một cách bất ngờ đầy thú vị trong một hương thơm với sắc hồng óng ánh, bên trong một thiết kế hình tròn, biểu tượng cho vòng tròn may mắn. Chance, hương thơm mới từ Chanel, là một kết hợp đầy bất ngờ, là sự va chạm của những làn sóng hương hoa tươi mát bất tận, toát lên một vẻ quyến rũ nồng nàn. Chanel Chance mang một tên gọi làm gợi lên một cảm giác thần thoại. Nếu Chanel và Chance mang những nét giống nhau, đó không chỉ là do may mắn, đó còn là vì chúng được sáng tạo nên để dành cho nhau. Như những đợt sóng tươi mát, hòa với hương hoa thơm ngát cùng những thành phần gia vị đặc biệt khiến cho Chanel Chance trở nên rất gơi cảm và ngọt ngào, nữ tính đến đáng kinh ngạc, dồi dào năng lượng sống, táo bạo pha chút gợi tình và nghịch ngợm.\r\n\r\nMùi hương đặc trưng: \r\n\r\nTiêu hồng, Chanh, Lan dạ hương, Hoa lài, Hoa irit, Hổ phách, Hoắc hương, Cỏ Vetiver, Xạ hương trắng.\r\n\r\nPhong cách: \r\n\r\nNữ tính, gợi cảm, tươi mát.', '25', null, '2015-09-24', null);
