INSERT INTO `products` VALUES ('110', 'Chloe Eau de Parfum\r\n', 'images/products/chloe/1369196173.png', null, '623', 'Cùng nhãn hiệu: Chloe\r\nChloe Eau de Parfum là một mùi hương mới của Chloe. Đây là một mùi hương nhẹ và tươi nhưng quyến rũ, mạnh mẽ, hương thơm của Chloe Eau de Parfum như một cô gái táo bạo và cá tính không “nhút nhát” như các sản phẩm trước nó\r\n\r\nHương thơm này rất linh hoạt, nó  phù hợp cho sử dụng hàng ngày  hơn là để trở thành hương thơm đặc trưng của một ai đó\r\n\r\nMùi hương đặt trưng:\r\n\r\nHương đầu: hoa mẫu đơn, hoa lan Nam Phi và trái vải\r\n\r\nHương giữa: hoa hồng, hoa lyli, hoa mộc lan\r\n\r\nHương cuối: xạ hương.', '36', null, '2015-09-24', null);
