INSERT INTO `products` VALUES ('116', 'Happy Spirit Elixir d\'amour\r\n', 'images/products/chopard/Chopard-happy-spirit-small.gì', '2', null, 'Cùng nhãn hiệu: Chopard\r\nNước hoa Happy Spirit với thiết kế hình trái tim hồng được lấy cảm hứng từ những chiếc nhẫn kim cương sang trọng và quyến rũ của tập đoàn Chopard. Mùi hương của Chopard Happy Spirit cũng sang trọng và quyến rũ đúng như vẻ ngoài của nó. Đó là sự kết hợp hài hòa của vị cam tươi mát quyện với hương hoa mộc lan và cây kim ngân tạo ra cảm giác khêu gợi, ngọt ngào thuần khiết khi bạn thoáng qua một ai đó.\r\n\r\nVới Happy Spirit, bạn sẽ có cảm giác như ở trên thiên đường bởi hương thơm ngọt ngào, lan tỏa miên man của nó. Sự hòa quyện tinh tế của xạ hương, hổ phách, hương gỗ… đã khiến cho Happy Spirit trở thành biểu tượng của sự ngọt ngào, nữ tính mà mọi cô gái đều ao ước.\r\n\r\nMùi hương đặc trưng: \r\n\r\nCây kim ngân hoa, quả mâm xôi, hoa mộc lan, hổ phách, gỗ và xạ hương.\r\n\r\nPhong cách:\r\n\r\nNgọt ngào, nữ tính.', '5', null, '2015-09-24', null);
