INSERT INTO `products` VALUES ('121', 'Millesime Imperial\r\n', 'images/products/creed/1378281896.png', null, null, 'Cùng nhãn hiệu: Creed\r\nTỏa sáng như ánh mặt trời rực rỡ vùng Địa Trung Hải, hương thơm kì thú này là sự pha trộn tuyệt vời của vị cam chanh sảng khoái và hương vị quyến rũ của vùng bờ biển Sicili. Hương thơm Millesime Imperial thật ấm áp và lãng mạn với những nốt hương cam chanh sống động, hương thơm của những loài hoa danh tiếng cùng một chút mùi vị biển khỏe khoắn.\r\n\r\nMùi hương đặc trưng:\r\n\r\nHương trái cây, Vị muối biển, Hương cam vùng Sicili, Quýt, Hoa Iris, Xạ hương, Hương gỗ.\r\n\r\nPhong cách:\r\n\r\nQuí phái, sang trọng', '10', null, '2015-09-24', '');
INSERT INTO `products` VALUES ('122', 'Intimately Beckham Yours', 'images/products/david beckham/1349489632.png', '85', '46', 'Cùng nhãn hiệu: David Beckham\r\nIntimately Yours Women là một hương thơm mới dành cho phụ nữ mang đậm phong cách phương đông và được sản xuất bởi David & Vitoria Beckham trong năm 2010.\r\n\r\nMùi hương đặc trưng:\r\n\r\nHương đầu cam Bergamot, Lan Nam phi, Lê; tầng hương giữa là cam Châu phi, Frangipani, bạch hoa; hương cuối là cổ Vetiver, hoa Iris, gỗ đàn hương.\r\n\r\nPhong cách:\r\n\r\nSang trọng, gợi cảm, quyến rũ.', '11', null, '2015-09-24', '');
INSERT INTO `products` VALUES ('123', 'Signature Story for Her\r\n', 'images/products/david beckham/2211033copy.jpeg', null, '5', 'Cùng nhãn hiệu: David Beckham\r\nĐược ra đời vào năm 2009, Signature Story for Her mang lại cho bạn một vẻ đẹp  thanh lịch của người phụ nữ. Tuyệt tác này lấy cảm hứng từ những bộ phim về tình yêu nổi tiếng của Hollywood, chính những điều này mang lại cho bạn cảm giác luôn được sống trong không khí của niềm hạnh phúc vô bờ bến.\r\n\r\nHương thơm đặc trưng:\r\n\r\nHương đào, hoa cam, xạ hương, hoa mẫu đơn trắng,....\r\n\r\nPhong cách:\r\n\r\nThanh lịch, nhẹ nhàng, quyến rũ,...', '12', null, '2015-09-24', null);
