INSERT INTO `products` VALUES ('148', 'Poison\r\n', 'images/products/dior/1340853559.png', '454', '53', 'Cùng nhãn hiệu: Dior\r\nTáo bạo và khêu gợi, khó hiểu và bí ẩn, Poison là loại nước hoa khiến người ta phải tốn nhiều giấy mực. Với mùi hương độc đáo, bức thông điệp Poison mang tới làm ta thấy mê say, và thậm chí ám ảnh. Thích hợp dùng buổi tối.\r\n\r\nMùi hương đặc trưng:\r\n\r\nDâu rừng, opoponax.\r\n\r\nPhong cách:\r\n\r\nKhêu gợi và táo bạo, quyến rũ khó cưỡng lại', '37', null, '2015-11-11', '');
