INSERT INTO `products` VALUES ('150', 'Pure Poison Elixir\r\n', 'images/products/dior/pure-possion-s.gif', '4564', '213', 'Cùng nhãn hiệu: Dior\r\nNước hoa tinh chất đậm đặc cho mùi hương lưu giữ thật lâu trên da. Hương thơm Pure Poison quen thuộc của Dior một lần nữa lại chinh phục bạn qua chùm hương họ cam chanh tươi mát dạo đầu với lá chanh và quýt xanh. Sau đó quí phái và lãng mạn với hoa cam và hương lài Sambac. Cuối cùng là tầng hỗn hợp hương thơm độc đáo của hổ phách, hạnh nhân, đàn hương, vani và cacao nguyên chất cho một cảm nhận ấm áp dễ chịu thật khó quên.\r\n\r\nMùi hương đặc trưng:\r\n\r\nLá chanh, Quýt xanh, Hoa cam, Lài Sambac, Đàn hương, Hổ phách, Hạnh nhân, Vani, Cacao nguyên chất\r\n\r\nPhong cách:\r\n\r\nQuí phái, sinh động', '2', null, '2015-11-11', null);
