INSERT INTO `products` VALUES ('159', 'Miss Dior Cherie L\'eau (2009)\r\n', 'images/products/dior/img-thing-01-m.gif', '46756876', null, 'Cùng nhãn hiệu: Dior\r\nMột cô gái Paris trẻ trung, duyên dáng, hờ hững, thanh lịch và vui tươi nhìn cuộc đời với ánh mắt tích cực. Với cô nơi đó không thể có điều xấu, tất cả đều được bao trùm bởi một màu tùng lam thanh bình. Niềm tin, vẻ duyên dáng và nhan sắc của cô ấy trở thành một biểu tượng của sự trẻ trung, tươi mới và tự lập. \"Miss Dior cherie L\'Eau\" không phải là một mùi thơm phức tạp.\" Francois Demachy đã có ý tưởng làm nổi bật vẻ đẹp giản dị. Đó chỉ là một công thức đơn giản của sự tươi mát, sạch sẽ và tinh xảo. Hương thơm này dành cho những cô gái trẻ trung, năng động và tràn ngập niềm vui, luôn bỏ qua những sắc màu xám xịt và đơn điệu, thay thế vào đó là những sắc màu tùng lam. \r\n\r\nMùi hương đặc trưng:\r\n\r\nHương đầu mang lại cảm giác khoan khóai với hương vị của cam- vừa mang một chút vị ngọt, 1 chút vị chua lại 1 chút vị cay. \r\nHương giữa chủ đạo là mùi hoa dành dành, một loài hoa trắng mỏng manh, tinh khiết.\r\nHương cuối là mùi xạ hương trắng, mang lại một cảm giác mềm mượt, tươi mới và sạch sẽ trên làn da bạn.\r\n\r\n\r\n', '11', null, '2015-11-11', null);
