INSERT INTO `products` VALUES ('178', 'Be Delicious Fresh Blossom', 'images/products/dkny/1344399926.png', '47', '234', ' Cùng nhãn hiệu: DKNY\r\nRa mắt vào năm 2009, Be Delicious Fresh Blossom là một điểm nhấn đầy ấn tượng.\r\n\r\nMùi hương đặc trưng:\r\n\r\nBưởi chùm, hoa nhài, quả mơ, hoa hồng, táo, gỗ vàng, mùi da mềm, hoa Muguet trắng.\r\n\r\nPhong cách:\r\n\r\nLôi cuốn, tinh xảo, tươi sáng.', '30', null, '2015-09-24', null);
