INSERT INTO `products` VALUES ('181', 'Delicious Candy Apples Juice Berry\r\n', 'images/products/dkny/1346400515.png', '534', null, 'Cùng nhãn hiệu: DKNY\r\nLấy cảm hứng từ những đêm New York rực rỡ ánh đèn, cuộc sống về đêm muôn màu nhộn nhịp. New York là một thành phố không bao giờ ngủ, càng vể đêm lại càng chứa đựng nhiều điều thú vị, nhiều bí ẩn luôn chờ được khám sau tấm màn nhung đen huyền bí. Tuy lấy đêm làm cảm hứng, nhưng Delicious Night lại chọn mẫu chai màu tím đậm với hình dáng quả táo “thương hiệu” của DKNY. Lý giải cho điều này, DKNY cho rằng họ chọn màu tím vì muốn thể hiện một buổi đêm huyền diệu lung linh, đâu đó phía sau những toà nhà sáng rực ánh đèn vẫn còn những góc khuất.\r\n\r\nMặt khác, chúng ta thường cho rằng màn đêm đi kèm với bóng tối và đôi khi gợi lên sự sợ hãi, Delicious Night được thể hiện bằng màu tím nhằm minh chứng cho một thành phố New York tràn đầy sức sống và luôn nhộn nhịp dù cho màn đêm có buông xuống. Màu tím huyền bí, lãng mạn và sang trọng là lựa chọn số một cho hình ảnh một đêm New York không ngủ.\r\n\r\nMùi hương đặc trưng:\r\n\r\nBưởi, Gừng, Lan Nam Phi, Mâm xôi, Rượu Martini, Dạ lan, Hoa lài, Hoa Iris, Hổ phách, Hoắc hương, Hương lau, Hương trầm, Nhựa thơm.\r\n\r\nPhong cách:\r\n\r\nQuyến rũ, huyền bí, sang trọng', '1', null, '2015-11-11', null);
