INSERT INTO `products` VALUES ('195', 'Harajuku Love\r\n', 'images/products/harajuku lovers/131545358255,jpeg', '63', '8', ' Cùng nhãn hiệu: Harajuku Lovers\r\nNgọt ngào, biết quan tâm, phong thái tự do, Love luôn vượt ra bên ngoài thế giới nhỏ bé của cô ấy. Bước đi trong chiếc váy sọc dễ thương với đôi giày đỏ thời trang, Love trang điểm thêm những trái tim trên tóc. Mùi hương này gợi nhớ hương thơm của một bó hoa cỏ với bưởi chùm, hoa hồng và hoa lan.\r\n\r\nHương thơm đặc trưng:\r\n\r\nHương đầu: cam, bưởi chùm, đào, tre.\r\n\r\nHương giữa: hoa mẫu đơn, hoa hồng, hoa nhài Ai Cập, hoa thủy tiên.\r\n\r\nHương cuối: hoa ngọc lan tây,vani, xạ hương, gỗ.\r\n\r\nPhong cách:\r\n\r\nDịu dàng, lãng mạn, quyến rũ.', '15', null, '2015-09-24', null);
