INSERT INTO `products` VALUES ('202', 'Hello Kitty Baby', 'images/products/hello kitty/1329119514.jpeg', null, '799', ' Cùng nhãn hiệu: Hello Kitty\r\nCó lẽ ai yêu thích nhân vật Hello Kitty sẽ không bỏ lỡ cơ hội có được sản phẩm nước hoa dễ thương này. Kiểu dáng chai đặc biệt vẫn có hình chú mèo Hello Kitty xinh xắn, màu hồng vẫn là màu chủ đạo của chú mèo xinh xắn này. Có rất nhiều tầng hương cho bạn lựa chọn.\r\n\r\nNước hoa Hello Kitty Baby với hương thơm tinh nghịch, trẻ trung.\r\n\r\nMùi hương đặc trưng:\r\n\r\nBánh, kẹo ngọt, trái cây, dâu tây...\r\n\r\nPhong cách:\r\n\r\nTrẻ trung, năng động', '22', null, '2015-11-11', null);
