DROP PROCEDURE IF EXISTS `activeAccount`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `activeAccount`(IN _username VARCHAR(20), IN _code VARCHAR(20), OUT _result TINYINT(1))
BEGIN
	IF(EXISTS(SELECT * FROM users WHERE users.username = _username AND users.`status` = _code)) THEN
		update users SET users.`status` = 'actived' WHERE users.username = _username;
		SET _result = 1;
	ELSE
		SET _result = 0;
	END IF;
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for checkLogin
-- ----------------------------
DROP PROCEDURE IF EXISTS `checkLogin`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `checkLogin`(IN _username VARCHAR(100), IN _password VARCHAR(100), IN _type VARCHAR(8), OUT _result VARCHAR(200))
BEGIN
	IF(_type != 'facebook') THEN

	IF(EXISTS(SELECT * FROM users WHERE users.username = _username)) THEN
		IF(EXISTS(SELECT * FROM users WHERE users.username = _username AND users.`password` = _password)) THEN
			IF(EXISTS(SELECT * FROM users WHERE users.username = _username AND users.`status` like '%actived')) THEN
				SET _result = "ok";
			ELSE
				SET _result = "Tài khoản chưa được xác thực, vui lòng kiểm tra email để xác thực";
			END IF;
		ELSE
				SET _result = "Tên tài khoản hoặc mật khẩu chưa đúng";
		END IF;
	ELSE
		SET _result = "Tài khoản không tồn tại trong hệ thống";
	END IF;

	ELSE
		IF(EXISTS(SELECT * FROM users WHERE users.username = _username)) THEN
			SET _result = "ok";
		ELSE
			SET _result = "error";
		END IF;
	END IF;
END
;;
DELIMITER ;

